import React, { Component } from 'react'
import './style.css'
import dataGlasses from './dataGlasses.json'

export default class BTGlasses extends Component {
//    renderGlasses =() =>{
//     return dataGlasses.map((glasses,index) => {
//         return <img className='w-100' src={glasses.url}  alt={glasses.name} />

//     })
//    }
    render() {
        const myImgBg = {
            backgroundImage: 'url(./img/background.jpg)',
            height:'100vh',
            backgroundSize: 'cover',
        }

        return (
            <div style={myImgBg}>
                <div className="bg">
                <div className="container">
                    <h1>Bài tập Glasses</h1>
                    <div className="model d-flex justify-content-around">
                        <div className="model_right">
                            <img className='img_active' src="./img/v1.png" alt="" />
                            <img className='img_model' src="./img/model.jpg" alt="..." />
                            <div className="text_content">
                                <h3>TÊN</h3>
                                <p>Mô tả</p>
                            </div>
                        </div>
                        <div  className="model_left" >
                            <img  className='img_model'  src="./img/model.jpg" alt="..." />

                        </div>
                    </div>
                    <div className="">
                        <div className="">
                            {
                                   renderGlasses =() =>{
                                    return dataGlasses.map((glasses,index) => {
                                        return <img className='w-100' src={glasses.url}  alt={glasses.name} />

                                    })
                                   }
                            }

                          {/* {this.renderGlasses()} */}

                        </div>
                    </div>
                </div>

                </div>
            </div>
        )
    }
}
